#' An abstraction that holds information needed to make an epidata request
#' @rdname epidata_call
#' @aliases epidata_call
#'
#' @description
#' `epidata_call` objects are generated by endpoint functions like
#' [`covidcast`]; should be piped into the `fetch` function to fetch and format
#' the data. For most endpoints this will return a tibble, but a few
#' non-COVIDCAST endpoints only support will return a JSON-like list instead.
#'
#' `create_epidata_call` is the constructor for `epidata_call` objects, but you
#' should not need to use it directly; instead, use an endpoint function, e.g.,
#' [`covidcast`], to generate an `epidata_call` for the data of interest.
#'
#' There are some other functions available for debugging and advanced usage:
#' - `request_url` (for debugging):  outputs the request URL from which data
#'    would be fetched (note additional parameters below)
#' - `with_base_url` (advanced): outputs an `epidata_call` that requests data
#'    from a different Epidata server
#'
#' @examples
#' covidcast(
#'   data_source = "jhu-csse",
#'   signals = "confirmed_7dav_incidence_prop",
#'   time_type = "day",
#'   geo_type = "state",
#'   time_values = epirange(20200601, 20200801),
#'   geo_values = c("ca", "fl")
#' ) %>% fetch()
#'
#' @param endpoint the epidata endpoint to call
#' @param params the parameters to pass to the epidata endpoint
#' @param meta meta data to attach to the epidata call
#' @param only_supports_classic if true only classic format is supported
#'
#' @return
#' - For `create_epidata_call`: an `epidata_call` object
#'
create_epidata_call <- function(endpoint, params, meta = NULL,
                                only_supports_classic = FALSE) {
  stopifnot(is.character(endpoint), length(endpoint) == 1)
  stopifnot(is.list(params))
  stopifnot(is.null(meta) || is.list(meta))
  stopifnot(is.logical(only_supports_classic), length(only_supports_classic) == 1)
  if (is.null(meta)) {
    meta <- list()
  }
  structure(
    list(
      endpoint = endpoint,
      params = params,
      base_url = global_base_url,
      meta = meta,
      only_supports_classic = only_supports_classic
    ),
    class = "epidata_call"
  )
}

#' @importFrom checkmate test_class test_list
request_arguments <- function(epidata_call, format_type, fields = NULL) {
  stopifnot(inherits(epidata_call, "epidata_call"))
  stopifnot(format_type %in% c("json", "csv", "classic"))
  stopifnot(is.null(fields) || is.character(fields))

  extra_params <- list()
  if (format_type != "classic") {
    extra_params[["format"]] <- format_type
  }
  if (!is.null(fields)) {
    extra_params[["fields"]] <- fields
  }
  all_params <- c(epidata_call$params, extra_params)

  formatted_params <- list()
  for (name in names(all_params)) {
    v <- all_params[[name]]
    if (!is.null(v)) {
      if (test_class(v, "EpiRange")) {
        formatted_params[[name]] <- format_item(v)
      } else if (test_list(v)) {
        formatted_params[[name]] <- format_list(v)
      } else {
        formatted_params[[name]] <- format_item(v)
      }
    }
  }
  formatted_params
}

#' @export
print.epidata_call <- function(epidata_call) {
  stopifnot(inherits(epidata_call, "epidata_call"))
  cli::cli_h1("<epidata_call> object:")
  cli::cli_bullets(c(
    "*" = "Pipe this object into `fetch()` to actually fetch the data",
    "*" = paste0("Request URL: ", request_url(epidata_call))
  ))
}

#' Fetches the data
#'
#' `fetch` usually returns the data in tibble format, but a few of the endpoints
#' only support the JSON classic format (delphi, pvt_meta_norostat, and meta).
#' In that case a JSON-like nested list structure is returned instead.
#'
#' @rdname epidata_call
#' @param epidata_call an instance of `epidata_call`
#' @param fields a list of epidata fields to return, or NULL to return all
#'   fields (default) e.g. c("time_value", "value") to return only the
#'   time_value and value fields or c("-direction") to return everything except
#'   the direction field
#' @param disable_date_parsing disable automatic date parsing
#' @param return_empty boolean that allows returning an empty tibble if there is no data.
#' @return
#' - For `fetch`: a tibble or a JSON-like list
#' @export
#'
fetch <- function(epidata_call, fields = NULL, disable_date_parsing = FALSE, return_empty = FALSE) {
  stopifnot(inherits(epidata_call, "epidata_call"))
  stopifnot(is.null(fields) || is.character(fields))
  stopifnot(is.logical(disable_date_parsing), length(disable_date_parsing) == 1)
  stopifnot(is.logical(return_empty))

  if (epidata_call$only_supports_classic) {
    return(fetch_classic(epidata_call, fields, return_empty))
  } else {
    return(fetch_tbl(epidata_call, fields, disable_date_parsing, return_empty))
  }
}

#' Fetches the data and returns a tibble
#' @rdname fetch_tbl
#'
#' @param epidata_call an instance of `epidata_call`
#' @param fields a list of epidata fields to return, or NULL to return all
#'   fields (default) e.g. c("time_value", "value") to return only the
#'   time_value and value fields or c("-direction") to return everything except
#'   the direction field
#' @param disable_date_parsing disable automatic date parsing
#' @param return_empty boolean that allows returning an empty tibble if there is no data.
#' @importFrom readr read_csv
#' @importFrom httr stop_for_status content
#' @importFrom rlang abort
#' @return
#' - For `fetch_tbl`: a [`tibble::tibble`]
#' @importFrom tibble as_tibble
#' @keywords internal
fetch_tbl <- function(epidata_call, fields = NULL, disable_date_parsing = FALSE, return_empty = FALSE) {
  stopifnot(inherits(epidata_call, "epidata_call"))
  stopifnot(is.null(fields) || is.character(fields))
  stopifnot(is.logical(disable_date_parsing), length(disable_date_parsing) == 1)
  stopifnot(is.logical(return_empty))

  if (epidata_call$only_supports_classic) {
    rlang::abort("This endpoint only supports the classic message format, due to a non-standard behavior. Use fetch_classic instead.",
      epidata_call = epidata_call,
      class = "only_supports_classic_format"
    )
  }

  response_content <- fetch_classic(epidata_call, fields, disable_data_frame_parsing = FALSE, return_empty = return_empty)
  if (return_empty && length(response_content) == 0) {
    return(tibble())
  }
  return(parse_data_frame(epidata_call, response_content, disable_date_parsing) %>% as_tibble())
}

#' Fetches the data, raises on epidata errors, and returns the results as a
#' JSON-like list
#'
#' @rdname fetch_classic
#'
#' @param epidata_call an instance of `epidata_call`
#' @param fields a list of epidata fields to return, or NULL to return all
#'   fields (default) e.g. c("time_value", "value") to return only the
#'   time_value and value fields or c("-direction") to return everything except
#'   the direction field
#' @param disable_data_frame_parsing do not automatically cast the epidata
#'   output to a data frame (some endpoints return a list of lists, which is not
#'   a data frame)
#' @param return_empty boolean that allows returning an empty tibble if there is no data.
#' @importFrom httr stop_for_status content http_error
#' @importFrom jsonlite fromJSON
#' @return
#' - For `fetch_classic`: a JSON-like list
#' @keywords internal
fetch_classic <- function(epidata_call, fields = NULL, disable_data_frame_parsing = TRUE, return_empty = FALSE) {
  stopifnot(inherits(epidata_call, "epidata_call"))
  stopifnot(is.null(fields) || is.character(fields))
  stopifnot(is.logical(return_empty))

  response <- request_impl(epidata_call, "classic", fields)
  response_content <- httr::content(response, as = "text", encoding = "UTF-8")

  response_content <- jsonlite::fromJSON(response_content, simplifyDataFrame = !disable_data_frame_parsing)

  # success is 1, no results is -2, truncated is 2, -1 is generic error
  if (response_content$result != 1) {
    if ((response_content$result != -2) && !(return_empty)) {
      rlang::abort(paste0("epidata error: ", response_content$message), "epidata_error")
    }
  }
  if (response_content$message != "success") {
    rlang::warn(paste0("epidata warning: ", response_content$message), "epidata_warning")
  }
  return(response_content$epidata)
}

fetch_debug <- function(epidata_call, format_type = "classic", fields = NULL) {
  response <- request_impl(epidata_call, format_type, fields)
  content <- httr::content(response, "text", encoding = "UTF-8")
  content
}

full_url <- function(epidata_call) {
  stopifnot(inherits(epidata_call, "epidata_call"))
  join_url(epidata_call$base_url, epidata_call$endpoint)
}

#' Returns the full request url for the given epidata_call
#' @rdname request_url
#'
#' @param epidata_call an instance of `epidata_call`
#' @param format_type format to return one of classic,json,csv
#' @param fields a list of epidata fields to return, or NULL to return all
#'   fields (default) e.g. c("time_value", "value") to return only the
#'   time_value and value fields or c("-direction") to return everything except
#'   the direction field
#' @importFrom httr modify_url
#' @return
#' - For `request_url`: string containing the URL
#' @keywords internal
request_url <- function(epidata_call, format_type = "classic", fields = NULL) {
  stopifnot(inherits(epidata_call, "epidata_call"))
  url <- full_url(epidata_call)
  params <- request_arguments(epidata_call, format_type, fields)
  httr::modify_url(url, query = params)
}

#' `epidata_call` object using a different base URL
#' @rdname epidata_call
#'
#' @param epidata_call an instance of `epidata_call`
#' @param base_url base URL to use
#' @return
#' - For `with_base_url`: an `epidata_call` object
#'
#' @export
with_base_url <- function(epidata_call, base_url) {
  stopifnot(inherits(epidata_call, "epidata_call"))
  stopifnot(is.character(base_url), length(base_url) == 1)
  epidata_call$base_url <- base_url
  epidata_call
}

#' Makes a request to the API and returns the response, catching
#' HTTP errors and forwarding the HTTP body in R errors
#' @importFrom httr stop_for_status content http_type
#' @importFrom xml2 read_html xml_find_all xml_text
request_impl <- function(epidata_call, format_type, fields = NULL) {
  stopifnot(inherits(epidata_call, "epidata_call"))
  stopifnot(format_type %in% c("json", "csv", "classic"))

  url <- full_url(epidata_call)
  params <- request_arguments(epidata_call, format_type, fields)
  response <- do_request(url, params)

  if (response$status_code != 200) {
    # 500, 429, 401 are possible
    msg <- "fetch data from API"
    if (httr::http_type(response) == "text/html") {
      # grab the error information out of the returned HTML document
      msg <- paste(msg, ":", xml2::xml_text(xml2::xml_find_all(
        xml2::read_html(content(response, "text")),
        "//p"
      )))
    }
    httr::stop_for_status(response, task = msg)
  }

  response
}
